import React from 'react'
import axios from 'axios'

const getAreas = async () => {
	const data = await axios.get('http://localhost:5000/api/areas')
	return data.data.Data
}

const getZones = async (id) => {
	const data = await axios.get('http://localhost:5000/api/zones')
	return data.data.Data
}

class App extends React.Component {
	state = {
		data: [],
	}

	start = async () => {
		const data = await getAreas()
		this.setState({data: data })
	}

	renderData = (data) => {
		return data.map(({AreaId, AreaName}) => {
			return <li key={AreaId}>{AreaId} : {AreaName} <button onClick={() => { this.update(AreaId) }}>Go</button></li>
		})
	}

	flipRender = () => {
		this.setState({ render: !this.state.render })
	}

	update = async (props) => {
		const zones = await getZones(props)
		this.setState((prevState) => {
			console.log(prevState)
			return {
				data: [props.data, ...zones]
			}
		})
	}


	render(){
		console.log(this.state.data)
		const display = this.state.data !== null ? this.renderData(this.state.data): null
		return (
			<div className="ui container comments">
				{this.state.data ? display : ""}
				<button onClick={this.start}>Submit</button>
			</div>
		)
	}
}

export default App
