import React from 'react'
import axios from 'axios'

const getAreas = async () => {
	const data = await axios.get('http://localhost:5000/api/areas')
	return data.data.Data
}

const getZones = async (id) => {
	const data = await axios.get('http://localhost:5000/api/zones')
	return data.data.Data
}


class App extends React.Component {
	state = {
		areaData: false
	}

	start = async () => {
		const data = await getAreas()
		this.setState({ areaData: data })
	}

	render(){
		console.log(this.state)
		return (
			<div className="ui container comments">
				{this.state.areaData &&	<AreaComp1 data={this.state.areaData} />}
				<div>
					<button onClick={this.start}>Submit</button>
					<button onClick={this.start}>Back</button>
				</div>
			</div>
		)
	}
}

class AreaComp1 extends React.Component {
	state = {
			zoneData: false
		}

	renderData = (data) => {
		return data.map(({AreaId, AreaName}) => {
			return <li key={AreaId}>{AreaId} : {AreaName} <button onClick={() => { this.update(AreaId) }}>Go</button></li>	
		})
	}

	update = async (props) => {
		const zones = await getZones(props)
		this.setState((prevState) => {
			return {
				zoneData: [ this.state.data, [...zones]]
			}
		})
	}

	render() {
		console.log(this.state)
		const areaData = this.renderData(this.props.data)
		return (
			<div>
				{areaData}
				{/* {this.state.zoneData &&	<ZoneComp2 data={this.state.zoneData} renderData={this.renderData} />}  */}
				{this.state.zoneData &&	<ZoneComp2 data={this.state.zoneData} />}

			</div>
		)
	}
}


class ZoneComp2 extends React.Component {
	state = {
			zoneData: false
		}

	renderData = (data) => {
		return data.map(({AreaId, AreaName}) => {
			return <li key={AreaId}>{AreaId} : {AreaName} <button onClick={() => { this.update(AreaId) }}>Go</button></li>	
		})
	}

	render() {

		//const areaData = this.renderData(this.props.data)
		return (
			<div>
				Zone Data

			</div>
		)
	}
}

export default App
