import React from 'react'
import axios from 'axios'

const getAreas = async () => {
	const data = await axios.get('http://localhost:5000/api/areas')
	return data.data.Data
}

const getZones = async (id) => {
	const data = await axios.get('http://localhost:5000/api/zones')
	console.log(data.data.Data)
	return data.data.Data
}

class App extends React.Component {
	state = {
		data: null,
		render: false,
		history: []
	}

	async componentDidMount(){
		const data = await getAreas()
		this.setState({data})
		this.updateHistory()
	}

	renderData = (data) => {
		return data.map(({AreaId, AreaName}) => {
			return <li key={AreaId}>{AreaId} : {AreaName} <button onClick={() => {}}>Go</button></li>
		})
	}

	flipRender = () => {
		this.setState({ render: !this.state.render })
	}

	updateHistory = () => {
		this.setState({history: [...this.state.data]})
	}

	render(){
		const display = this.state.data !== null ? this.renderData(this.state.data): null
		console.log(this.state.history)
		return (
			<div className="ui container comments">
				{this.state.render ? display : ""}
				<button onClick={this.flipRender}>Submit</button>
			</div>
		)
	}
}

export default App
