import React from 'react'
import axios from 'axios'

const getAreas = async () => {
	const data = await axios.get('http://localhost:5000/api/areas')
	return data.data.Data
}

const getZones = async (id) => {
	const data = await axios.get('http://localhost:5000/api/zones', { params: { id }})
	return data.data.Data
}

const getCategory = async (id) => {
	const data = await axios.get('http://localhost:5000/api/category', { params: { id }})
	return data.data.Data
}


class App extends React.Component {
	state = {
		areaData: undefined
	}

	start = async () => {
		const data = await getAreas()
		this.setState({ areaData: data })
	}

	render(){
		//console.log(this.state)
		return (
			<div className="ui container comments">
				{this.state.areaData &&	<AreaComp1 data={this.state.areaData} />}
				<div>
					<button onClick={this.start}>Submit</button>
					<button onClick={this.start}>Back</button>
				</div>
			</div>
		)
	}
}

class AreaComp1 extends React.Component {
	state = {
			zoneData: undefined
		}

	renderData = (data) => {
		return data.map(({AreaId, AreaName}) => {
			return <li key={AreaId}>{AreaId} : {AreaName} <button onClick={() => { this.update(AreaId) }}>Go</button></li>	
		})
	}

	update = async (props) => {
		console.log(props)
		const zones = await getZones(props)
		this.setState({ zoneData: zones })
	}

	clearState = () => {
		this.setState({zoneData: undefined})
	}

	render() {
		const areaData = this.renderData(this.props.data)
		//console.log(this.state)
		return (
			<div>
				Area Data:
				{areaData}
				<button onClick={this.clearState}>Clear</button>
				{this.state.zoneData &&	<ZoneComp2 data={this.state.zoneData} />}

			</div>
		)
	}
}

class ZoneComp2 extends React.Component {
	state = {
			categoryData: false
		}

	update = async (props) => {
		const zones = await getZones(props)
		this.setState({ categoryData: zones })
	}	

	clearState = () => {
		this.setState({zoneData: undefined})
	}

	renderData = (data) => {
		return data.map(({ZoneId, ZoneName}) => {
			return <li key={ZoneId}>{ZoneId} : {ZoneName} <button onClick={() => { this.update(ZoneId) }}>Go</button></li>	
		})
	}

	render() {
		const zoneData = this.renderData(this.props.data)
		return (
			<div>
				Zone Data:
				{zoneData}
				<button onClick={this.clearState}>Clear</button>
				{this.state.categoryData &&	<CategoryComp3 data={this.state.categoryData} />}

			</div>
		)
	}
}

class CategoryComp3 extends React.Component {
	state = {
			categoryData: false
		}

	update = async (props) => {

		const zones = await getZones(props)
		this.setState({ zoneData: zones })
	}

	renderData = (data) => {
		return data.map(({ZoneId, ZoneName}) => {
			return <li key={ZoneId}>{ZoneId} : {ZoneName} <button onClick={() => { }}>Go</button></li>	
		})
	}

	render() {
		console.log(this.props.data)
		//const categoryData = this.renderData(this.props.data)
		return (
			<div>
				Category Data:
				{}
			</div>
		)
	}
}


export default App
