import React from 'react'
import { getAreas } from '../services/apiCalls'

class AreaComponent extends React.Component {
	state = {
			areaData: undefined
		}

	componentDidMount(){
		const data = getAreas()
		this.setState({ areaData: data })
	}


	renderData = (data) => {
		return data.map(({AreaId, AreaName}) => {
			return <li key={AreaId}>{AreaId} : {AreaName} <button onClick={() => { this.update(AreaId) }}>Go</button></li>	
		})
	}

	// update = async (props) => {
	// 	console.log(props)
	// 	const zones = await getZones(props)
	// 	this.setState({ zoneData: zones })
	// }

	clearState = () => {
		this.setState({zoneData: undefined})
	}

	render() {
		const areaData = this.state.areaData && this.renderData(this.props.data)
		//console.log(this.state)
		return (
			<div>
				Area Data:
				{areaData}
				<button onClick={this.clearState}>Clear</button>


			</div>
		)
	}
}

export default AreaComponent
