import React from 'react'
import AreaComponent from './Area'
import { getAreas } from '../services/apiCalls'
import axios from 'axios'

class App extends React.Component {
	state = {
		areaData: undefined,
		zoneData: undefined
	}

	onClick = async () => {
		const token = 'UZ5UpPdz4bxnsH45wHkE6sjkbY2PckQgTBqZNJJF8lwctqWtDq00edfqIRf7vl0N'
		const resp = await axios.get(`http://testcountingapi.jjfoodservice.com/api/Area/v1/All?ApiKey=TrKoMYSjArx5P1eg&icEmployee=00008190&icLocation=EN-MW&type=json&version=V1&token=${token}`)
		this.setState({ areaData: resp.data.Data})
	}

	getID = (id) => {
		this.setState({ zoneData: id })
	}

	render(){
		console.log(this.state)
		return (
			<div className="ui container comments">
				<button onClick={this.onClick}>Start</button> 
				{this.state.areaData && <AreaComponent data={this.state.areaData} getID ={this.getID} />}
				{/* {this.state.zoneData && <AreaComponent data={this.state.zoneData} />} */}

			</div>
		)
	}
}


